/* eslint-disable import/no-anonymous-default-export */

// ° Siempre se retorna una copia del state -> ...state

import {
  FORMULARIO_PROYECTO,
  OBTENER_PROYECTOS,
  AGREGAR_PROYECTO,
  VALIDAR_FORMULARIO,
  PROYECTO_ACTUAL
} from "../../types";

export default ( state, action ) => {
  switch ( action.type ) {
    case FORMULARIO_PROYECTO:
      return {
        ...state,
        formulario: true
      }

    case OBTENER_PROYECTOS:
      return {
        ...state,
        proyectos: action.payload
      }

    case AGREGAR_PROYECTO:
      return {
        ...state,
        proyectos: [ ...state.proyectos, action.payload ],
        formulario: false,
        errorFormulario: false
      }

    case VALIDAR_FORMULARIO:
      return {
        ...state,
        errorFormulario: true
      }

    case PROYECTO_ACTUAL:
      return {
        ...state,
        // Filtra si el id del proyecto pulsado es igual al de proyecto de la lista, devuelve el proyecto seleccionado
        proyecto: state.proyectos.filter( proyecto => proyecto.id === action.payload )
      }

    default:
      return state;
  }
}
