import {
  TAREAS_PROYECTO
} from "../../types";


export default ( state, action ) => {
  switch ( action.type ) {
    case TAREAS_PROYECTO:
      return {
        ...state,
        tareasProyecto: state.tareas.filter( tarea => tarea.proyecto )
      }
    default:
      return state;
  }
}
