import { useContext, useState } from "react";
import ProyectoContext from "../../context/proyectos/ProyectoContext";
import TareaContext from "../../context/tareas/TareaContext";

const FormTarea = () => {

  // Extraer si un proyecto est치 activo
  const proyectosContext = useContext( ProyectoContext );
  const { proyecto } = proyectosContext;

  // Obtener la funcion del context de tarea
  const tareasContext = useContext( TareaContext );
  const { errorTarea, agregarTarea, validarTarea } = tareasContext;

  // State del formulario
  const [ tarea, setTarea ] = useState( {
    nombre: ''
  } )

  // Extraer el nombre del proyecto
  const { nombre } = tarea;

  // Si no hay proyecto seleccionado
  if ( !proyecto ) {
    return null
  }

  // Array destructuring para extraer el proyecto actual
  const [ proyectoActual ] = proyecto;

  // Leer los valores del formulario
  const handleChange = e => {
    setTarea( {
      ...tarea,
      [ e.target.name ]: e.target.value
    } )
  }

  const onSubmit = e => {
    e.preventDefault();

    // Validar
    if ( nombre.trim() === '' ) {
      validarTarea();
      return;
    }

    // Pasar la validaci칩n

    // Agregar la tarea nueva al state de tareas, se iguala el id y se pasan las tareas en false
    tarea.proyectoId = proyectoActual.id;
    tarea.estado = false;
    agregarTarea( tarea );

    // Reiniciar el Form

  }

  // Mostrar Error con timer
  const mostrarError = () => {
    setTimeout( () => {
      return <p>Hola</p>
    }, 3000 );
  }

  return (
    <div className="formulario">
      <form
        onSubmit={ onSubmit }
      >

        <div className="contenedor-input">
          <input
            type="text"
            name="nombre"
            id=""
            className="input-text"
            placeholder="Nombre Tarea..."
            value={ nombre }
            onChange={ handleChange }
          />
        </div>

        <div className="contenedor-input">
          <input
            type="submit"
            value="Agregar Tarea"
            className="btn btn-primario btn-submit btn-block"
          />
        </div>

      </form>

      { errorTarea ?
        <p className="mensaje error" onLoad={ mostrarError }>

        </p>
        :
        null
      }

    </div>
  );
}

export default FormTarea;
